mapper.select:

    SELECT DISTINCT P.id_persona, P.ci_persona, P.nombre_completo, P.sexo, P.celular,  P.correo_electronico
	FROM personas P
	JOIN  Personas_roles_Carreras PRC
	ON P.id_persona = prc.id_persona
	where CAST(prc.gestion AS INT) between #{v_anio_inicio,jdbcType=INTEGER} and #{v_anio_fin,jdbcType=INTEGER}
	AND PRC.id_persona_rol NOT IN (
	    SELECT id_persona_rol
	    FROM Eventos_Personas_Roles_Carreras
	    where id_evento   = #{v_id_evento,jdbcType=INTEGER}
	)
	AND (lower(p.nombre_completo) like lower(#{v_palabra_busqueda,jdbcType=VARCHAR}) or lower(p.ci_persona) like lower(#{v_palabra_busqueda,jdbcType=VARCHAR})   );
